import { createSlice } from '@reduxjs/toolkit';

const proxyHost = '127.0.0.1';
const proxyPort = 3500;

// Proxy URL
const proxyUrl = `http://${ proxyHost }:${ proxyPort }`;

const initialState = [];

const eventsSlice = createSlice( {
    name: 'events',
    initialState: {
        events: initialState
    },
    reducers: {
        loadEvents: ( state, action ) => {
            // check to see if there are any new events
            // if so, add them to the state
            // if not, do nothing
            if ( state.events.length === 0 ) {
                state.events = action.payload;
            } else {
                let temp = state.events.filter( event => event.id !== action.payload.id );
                if ( temp.length !== state.events.length ) {
                    state.events = action.payload;
                }
            }
        },
        addEvent: ( state, action ) => {
            let newEvent = {};
            newEvent.name = action.payload.name;
            newEvent.eventDate = action.payload.eventDate;

            let evt = addNewEvent( newEvent );

            if ( evt !== null ) {
                state.events.push( evt );
            }
        },
        deleteEvent: ( state, action ) => {
            const eventId = action.payload;
            state.events = state.events.filter( event => event.id !== eventId );
        },
        updateEvent: ( state, action ) => {
            let modifiedEvent = {};
            modifiedEvent.id = action.payload.id;
            modifiedEvent.name = action.payload.name;
            modifiedEvent.eventDate = action.payload.eventDate;
            let dispatch = action.payload.dispatch;

            modifyEvent( modifiedEvent );

            let newState = [ ...state.events ];
            const index = newState.findIndex( event => event.id === modifiedEvent.id );

            if ( index === -1 ) {
                console.log( "Event not found" );
                fetchEvents( dispatch, null );
            } else {

                console.log( "index: " + index );
                console.log( "newState: " + newState );
                console.log( "modifiedEvent: " + modifiedEvent );

                newState[ index ].id = modifiedEvent.id;
                newState[ index ].name = modifiedEvent.name;
                newState[ index ].eventDate = modifiedEvent.eventDate;

                state.events = newState;
            }
        }
    }
} );

// Write a function that has `dispatch` and `getState` as arguments
const fetchEvents = ( dispatch, getState ) => {
    // Make an async HTTP request
    return fetch( 'http://127.0.0.1:3500/events', {
        method: "GET",
        headers: { "Content-Type": "application/json" },
        cache: "no-cache" // *default, no-cache, reload, force-cache, only-if-cached
    } ).then( res => {
        console.log( res );
        const evts = res.json();
        console.log( evts );
        return evts;
    } ).then( evts => {
        dispatch( { type: "events/loadEvents", payload: evts } );
    } ).catch( err => console.log( err ) );
    // todo, error condition

}

function removeEvent( id, dispatch ) {
    //console.log( "deleteEvent: " + id );
    return fetch( `http://127.0.0.1:3500/event/${ id }`, {
        method: "DELETE",
        headers: { "Content-Type": "application/json" },
        cache: "no-cache" // *default, no-cache, reload, force-cache, only-if-cached
    } )
        .then( response => {
            if ( response.ok ) {
                dispatch( { type: "events/deleteEvent", payload: id } );
            } else {
                console.log( response );
                console.log( "Event not deleted" );
            }
        } )
        .catch( error => {
            console.log( error );
        } );
}

function addNewEvent( newEvent ) {
    return fetch( `http://
${ proxyHost }:${ proxyPort }/event`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        body: JSON.stringify( newEvent )
    } )
        .then( response => {
            if ( response.ok ) {
                return response.json();
            } else {
                console.log( response );
                console.log( "Event not added" );
                return null;
            }
        } )
        .catch( error => {
            console.log( error );
            return null;
        } );
}

function modifyEvent( updatedEvent ) {
    return fetch( `http://
${ proxyHost }:${ proxyPort }/event/${ updatedEvent.id }`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        body: JSON.stringify( updatedEvent )
    } )
        .then( response => {
            if ( response.ok ) {
                return response.json();
            } else {
                console.log( response );
                console.log( "Event not updated" );
                return null;
            }
        } )
        .catch( error => {
            console.log( error );
            return null;
        } );
}

export const { loadEvents, addEvent, deleteEvent, updateEvent } = eventsSlice.actions;

export { fetchEvents, removeEvent };

export default eventsSlice.reducer;
