import React, { useEffect } from 'react';
import { Button, Card, Form } from 'react-bootstrap';
import { Container, Row, Col } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { fetchEventDetail, modifyEvent } from '../../features/events/eventDetailSlice';
import { Breadcrumb } from 'react-bootstrap';
import '../../app/App.css';


const selectEventDetail = state => state.currentEvent;

function AdminManageEventQuestions() {
    const eventDetailState = useSelector( selectEventDetail );

    const dispatch = useDispatch();

    const navigate = useNavigate();

    const { id } = useParams();

    useEffect( () => {
        fetchEventDetail( dispatch, eventDetailState, id );
    }, [ dispatch, eventDetailState, id ] );


    function handleSubmit( e, eventId ) {
        e.preventDefault()
        let eventName = e.currentTarget.UpdateEventName.value;
        let eventDate = e.currentTarget.UpdateEventDate.value;

        let updatedEvent = {};
        updatedEvent.id = eventId;
        updatedEvent.name = eventName;
        updatedEvent.eventDate = eventDate;

        modifyEvent( updatedEvent, dispatch );

        navigate( '/admin' );
    }


    return (
        <>
            <Breadcrumb>
                <Breadcrumb.Item href="/admin">Admin Dashboard</Breadcrumb.Item>
                <Breadcrumb.Item active>Manage Event Questions</Breadcrumb.Item>
            </Breadcrumb>
            <Container bg="light">
                <h2 className="md-auto text-center p-4">Manage Event Questions</h2>
                <Row className="mb-3">
                    <Col className="mb-3">
                        < Card border="secondary"
                            bg={ 'light' }
                            key={ eventDetailState.currentEvent.id } >
                            <Card.Body>
                                <Card.Title >{ eventDetailState.currentEvent.name }</Card.Title>
                                <p>Event Date: { eventDetailState.currentEvent.eventDate }</p>
                                <Form className="EventDetailForm" id="EventDetailForm">
                                    <Form.Group className="mb-3" controlId="formEventDetail" onKeyDown={ ( evt ) => processAddQuestion( evt, dispatch ) } >
                                        <table className="question-table" id="question-table">
                                            <thead>
                                                <tr>
                                                    <th align="center">Question</th>
                                                    <th align="center">Ranking</th>
                                                    <th align="center"></th>
                                                    <th colSpan="4" align="center">Rating</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                { ( eventDetailState.currentEvent.questions !== null && eventDetailState.currentEvent.questions !== undefined ) ? [ ...eventDetailState.currentEvent.questions ].sort( ( a, b ) =>
                                                    b.ranking - a.ranking
                                                ).map( qVal => (
                                                    < tr key={ qVal.qid } >
                                                        <td>{ qVal.question }</td>
                                                        <td>{ qVal.ranking }</td>
                                                        <td colSpan="5">
                                                            <ButtonGroup className="like-buttons">
                                                                { radios.map( ( radio, idx ) => (
                                                                    <ToggleButton
                                                                        key={ qVal.qid + "-" + idx }
                                                                        id={ `radio-${ qVal.qid }-${ idx }` }
                                                                        type="radio"
                                                                        variant="primary"
                                                                        name={ `radio-${ qVal.qid }` }
                                                                        value={ radio.value }
                                                                        disabled={ setButtonGroupState( eventDetailState.currentEvent.id, qVal.qid, idx ) }
                                                                        onChange={ ( e ) => processButtonChange( e, eventDetailState.currentEvent.id, qVal.qid, dispatch )
                                                                        }
                                                                    >
                                                                        { radio.name }
                                                                    </ToggleButton>
                                                                ) ) }
                                                            </ButtonGroup>

                                                        </td>
                                                    </tr>
                                                ) ) : <tr><td colSpan="7"><p>No Questions Found</p></td></tr> }
                                            </tbody>
                                        </table>

                                        <div>
                                            <Form.Label>Question</Form.Label>
                                            <Form.Control type="text" placeholder="Enter question" />
                                        </div>
                                    </Form.Group>
                                </Form>
                            </Card.Body>
                        </Card>
                    </Col >
                </Row >

            </Container >
        </>
    );
}

function stringToDate( dateString ) {
    //console.log( "stringToDate() dateString: " + dateString );
    if ( dateString === null || dateString === undefined ) {
        return new Date();
    }

    var parts = dateString.split( '-' )
    let dt = new Date();
    dt.setFullYear( parts[ 0 ] );
    dt.setMonth( parts[ 1 ] - 1 );
    dt.setDate( parts[ 2 ] );
    //console.log( dt );
    return dt;
}

export default AdminManageEventQuestions;
