import React, { useEffect } from 'react';
import { Button, Card, Form } from 'react-bootstrap';
import { Container, Row, Col } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { fetchEventDetail } from '../../features/events/eventDetailSlice';
import { Breadcrumb } from 'react-bootstrap';
import '../../app/App.css';

const selectEventDetail = state => state.currentEvent;

function AdminUpdateEventDetail() {
    const eventDetailState = useSelector( selectEventDetail );

    const dispatch = useDispatch();

    const { id } = useParams();

    useEffect( () => {
        fetchEventDetail( dispatch, eventDetailState, id );
    }, [ dispatch, eventDetailState, id ] );

    function handleSubmit() {
        console.log( "Updateing Event Detail" );

        //updateEventDetail( dispatch, eventDetailState, id );
    }

    return (
        <>
            <Breadcrumb>
                <Breadcrumb.Item href="/admin">Admin Dashboard</Breadcrumb.Item>
                <Breadcrumb.Item active>Update Event Detail</Breadcrumb.Item>
            </Breadcrumb>
            <Container bg="light">
                <h2 className="md-auto text-center p-4">Update Event Detail</h2>
                <Row className="mb-3">
                    <Col className="mb-3">
                        < Card border="secondary"
                            bg={ 'light' }
                            key={ eventDetailState.currentEvent.id } >
                            <Card.Body>
                                <Card.Title >{ eventDetailState.currentEvent.name }</Card.Title>
                                <br></br>
                                <p>{ eventDetailState.currentEvent.eventDate }</p>
                                <Form className="UpdateEventDetailForm" id="UpdateEventDetailForm" onSubmit={ handleSubmit() } >
                                    <Form.Group className="mb-3" controlId="formUpdateEventDetail">
                                        <Form.Label>Event Name</Form.Label>
                                        <Form.Control type="text" placeholder="Enter event name" defaultValue={ eventDetailState.currentEvent.name } />
                                        <Form.Text className="text-muted">
                                            Enter the name of the event.
                                        </Form.Text>
                                        <br></br>
                                        <br></br>
                                        {/*
                                        <Form.Label>Event Description</Form.Label>
                                        <Form.Control type="text" placeholder="Enter event description" defaultValue={ eventDetailState.currentEvent.description } />
                                        <Form.Text className="text-muted">
                                            Enter the description of the event.
                                        </Form.Text>
    */}
                                        <Form.Label>Event Date</Form.Label>
                                        <Form.Control type="date" placeholder="Enter event date" defaultValue={ new Date( eventDetailState.currentEvent.eventDate ).toISOString().substring( 0, 10 ) } />
                                        <Form.Text className="text-muted">
                                            Enter the date of the event.
                                        </Form.Text>
                                    </Form.Group>
                                    <Button variant="primary" type="submit">
                                        Submit
                                    </Button>
                                    <Button variant="secondary" type="cancel">
                                        Cancel
                                    </Button>
                                </Form>
                            </Card.Body>
                        </Card>
                    </Col >
                </Row >

            </Container >
        </> );
}



export default AdminUpdateEventDetail;
